import './App.css';
import { useState } from "react";

function App() {
  const [name, setName] = useState(""); // Quản lý input bằng state
  const [displayName, setDisplayName] = useState(""); // Lưu tên khi bấm nút

  function hello(){

    setDisplayName(name);
    return(
      <div>

        <h2>Hello {name}</h2>
      </div>
    );
  }
  return (
    <div>
      <label>Nhập tên của bạn: </label>
      <input type='text' id='name' value={name} onChange={(e) => setName(e.target.value)} ></input>
      <button onClick={hello}><h3>Hello?</h3></button>
      <div className='box'> Hello {name}</div>
    </div>
  );
}

export default App;
